package mypack;

public class PrimmeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		        // Print numbers from 1 to 10 in a single row with one tab space
		        for (int i = 1; i <= 10; i++) {
		            System.out.print(i + "\t"); // Print number followed by a tab (\t)
		        }
		        System.out.println(); // Move to the next line after printing all numbers
		    }
		


	}


